import React, { useState } from 'react';
import { ref, get, update } from 'firebase/database';
import { db } from '../config/firebase';
import '../assets/GantiPassword.css';

// Ambil userId dari localStorage/session
function getUserId() {
  return localStorage.getItem('username') || '';
}

function GantiPassword() {
  const [form, setForm] = useState({
    current: '',
    new: '',
    confirm: ''
  });
  const [notif, setNotif] = useState({ type: '', message: '' });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setNotif({ type: '', message: '' });

    // Validation
    if (!form.current || !form.new || !form.confirm) {
      setNotif({ type: 'error', message: 'Semua field wajib diisi.' });
      return;
    }

    if (form.new !== form.confirm) {
      setNotif({ type: 'error', message: 'Konfirmasi password tidak sama.' });
      return;
    }

    if (form.new.length < 6) {
      setNotif({ type: 'error', message: 'Password baru minimal 6 karakter.' });
      return;
    }

    if (form.current === form.new) {
      setNotif({ type: 'error', message: 'Password baru tidak boleh sama dengan password lama.' });
      return;
    }

    const userId = getUserId();
    if (!userId) {
      setNotif({ type: 'error', message: 'User tidak ditemukan. Silakan login ulang.' });
      return;
    }

    setLoading(true);

  const userRef = ref(db, `users/${userId}`);

    try {
      const snap = await get(userRef);
      const data = snap.val();

      if (!data || !data.password) {
        setNotif({ type: 'error', message: 'Data user tidak ditemukan.' });
        setLoading(false);
        return;
      }

      if (String(form.current).trim() !== String(data.password).trim()) {
        setNotif({ type: 'error', message: 'Password saat ini salah.' });
        setLoading(false);
        return;
      }

      await update(userRef, {
        password: form.new,
        passwordUpdatedAt: new Date().toISOString(),
      });

      setNotif({ type: 'success', message: 'âœ… Password berhasil diubah! Silakan gunakan password baru untuk login.' });
      setForm({ current: '', new: '', confirm: '' });
    } catch (err) {
      console.error('Error updating password:', err);
      setNotif({ type: 'error', message: 'Gagal mengubah password. Silakan coba lagi.' });
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setForm({ current: '', new: '', confirm: '' });
    setNotif({ type: '', message: '' });
  };

  return (
    <div className="ganti-password-container">
      <h2 className="page-title">Ganti Password</h2>
      <div className="page-subtitle">
        Perbarui kata sandi Anda untuk menjaga keamanan akun
      </div>

      <div className="ganti-password-content">
        {/* Notification */}
        {notif.message && (
          <div className={`notification-alert ${notif.type}`}>
            {notif.message}
          </div>
        )}

        {/* Form Card */}
        <div className="password-form-card">
          {/* Security Tip */}
          <div className="security-tip-box">
            <div className="security-tip-title">Tips Keamanan</div>
            <p className="security-tip-text">
              Gunakan password yang kuat dengan kombinasi huruf besar, kecil, angka, dan simbol. 
              Jangan gunakan password yang sama dengan akun lain.
            </p>
          </div>

          {/* Form */}
          <form className="password-form" onSubmit={handleSubmit}>
            <div className="form-group-password">
              <label className="form-label-password">Password Saat Ini</label>
              <input
                type="password"
                name="current"
                value={form.current}
                onChange={handleChange}
                placeholder="Masukkan password saat ini"
                className="form-input-password"
                disabled={loading}
                autoComplete="current-password"
              />
            </div>

            <div className="form-group-password">
              <label className="form-label-password">Password Baru</label>
              <input
                type="password"
                name="new"
                value={form.new}
                onChange={handleChange}
                placeholder="Masukkan password baru"
                className="form-input-password"
                disabled={loading}
                autoComplete="new-password"
              />
            </div>

            <div className="form-group-password">
              <label className="form-label-password">Konfirmasi Password Baru</label>
              <input
                type="password"
                name="confirm"
                value={form.confirm}
                onChange={handleChange}
                placeholder="Konfirmasi password baru"
                className="form-input-password"
                disabled={loading}
                autoComplete="new-password"
              />
            </div>

            {/* Password Requirements */}
            <div className="password-requirements">
              <div className="requirements-title">Persyaratan Password:</div>
              <ul className="requirements-list">
                <li>Minimal 6 karakter</li>
                <li>Kombinasi huruf besar dan kecil</li>
                <li>Mengandung angka atau simbol</li>
                <li>Tidak sama dengan password lama</li>
              </ul>
            </div>

            {/* Actions */}
            <div className="form-actions">
              <button
                type="button"
                onClick={handleReset}
                className="btn-reset-password"
                disabled={loading}
              >
                Batal
              </button>
              <button
                type="submit"
                className="btn-save-password"
                disabled={loading}
              >
                {loading ? 'Menyimpan...' : 'Simpan'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default GantiPassword;
