import React, { useState } from 'react';
import { Brain, Mail, Lock, Sparkles, ArrowRight } from 'lucide-react';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [focusedField, setFocusedField] = useState('');

  const handleSubmit = () => {
    setError('');
    setLoading(true);

    const name = username.trim();
    if (!name || !password) {
      setError('Username dan password wajib diisi!');
      setLoading(false);
      return;
    }

    setTimeout(() => {
      if (name.toLowerCase() === 'demo' && password === 'demo') {
        alert('Login berhasil! Username: ' + name);
      } else {
        setError('Username atau password salah. Coba: demo/demo');
      }
      setLoading(false);
    }, 1500);
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      
      {/* Animated Blobs */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '300px',
        height: '300px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '50%',
        filter: 'blur(60px)',
        animation: 'blob 7s infinite'
      }}></div>
      
      <div style={{
        position: 'absolute',
        top: 0,
        right: 0,
        width: '300px',
        height: '300px',
        background: 'rgba(255, 255, 200, 0.1)',
        borderRadius: '50%',
        filter: 'blur(60px)',
        animation: 'blob 7s infinite 2s'
      }}></div>

      {/* Login Card */}
      <div style={{
        width: '100%',
        maxWidth: '450px',
        background: 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(20px)',
        borderRadius: '24px',
        boxShadow: '0 25px 50px rgba(0, 0, 0, 0.25)',
        overflow: 'hidden',
        position: 'relative',
        zIndex: 10
      }}>
        
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #6366f1 0%, #a855f7 50%, #ec4899 100%)',
          padding: '40px 30px',
          textAlign: 'center',
          position: 'relative'
        }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '80px',
            height: '80px',
            background: 'rgba(255, 255, 255, 0.2)',
            borderRadius: '20px',
            marginBottom: '15px'
          }}>
            <Brain style={{ width: '40px', height: '40px', color: 'white' }} strokeWidth={2.5} />
          </div>
          
          <h1 style={{
            fontSize: '32px',
            fontWeight: 'bold',
            color: 'white',
            margin: '0 0 10px 0'
          }}>
            AI Planner
          </h1>
          
          <div style={{
            color: 'rgba(255, 255, 255, 0.9)',
            fontSize: '14px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '8px'
          }}>
            <Sparkles style={{ width: '16px', height: '16px' }} />
            <span>Asisten Pribadi Cerdas Anda</span>
            <Sparkles style={{ width: '16px', height: '16px' }} />
          </div>
        </div>

        {/* Form Section */}
        <div style={{ padding: '35px 30px' }}>
          <h2 style={{
            fontSize: '24px',
            fontWeight: 'bold',
            color: '#1f2937',
            textAlign: 'center',
            margin: '0 0 8px 0'
          }}>
            Selamat Datang Kembali! ðŸ‘‹
          </h2>
          
          <p style={{
            color: '#6b7280',
            fontSize: '14px',
            textAlign: 'center',
            margin: '0 0 25px 0'
          }}>
            Masuk untuk mengatur jadwal Anda
          </p>

          {/* Error Message */}
          {error && (
            <div style={{
              background: 'linear-gradient(to right, #ef4444, #ec4899)',
              color: 'white',
              padding: '15px',
              borderRadius: '12px',
              fontSize: '14px',
              marginBottom: '20px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <div style={{
                width: '6px',
                height: '6px',
                background: 'white',
                borderRadius: '50%'
              }}></div>
              {error}
            </div>
          )}

          {/* Username Input */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{
              display: 'block',
              fontSize: '14px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '8px'
            }}>
              Username
            </label>
            <div style={{ position: 'relative' }}>
              <Mail style={{
                position: 'absolute',
                left: '16px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '20px',
                height: '20px',
                color: focusedField === 'username' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="text"
                placeholder="Masukkan username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                onFocus={() => setFocusedField('username')}
                onBlur={() => setFocusedField('')}
                style={{
                  width: '100%',
                  padding: '14px 16px 14px 48px',
                  background: focusedField === 'username' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'username' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '12px',
                  fontSize: '16px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'username' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
          </div>

          {/* Password Input */}
          <div style={{ marginBottom: '25px' }}>
            <label style={{
              display: 'block',
              fontSize: '14px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '8px'
            }}>
              Password
            </label>
            <div style={{ position: 'relative' }}>
              <Lock style={{
                position: 'absolute',
                left: '16px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '20px',
                height: '20px',
                color: focusedField === 'password' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="password"
                placeholder="Masukkan password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onFocus={() => setFocusedField('password')}
                onBlur={() => setFocusedField('')}
                onKeyPress={(e) => e.key === 'Enter' && handleSubmit()}
                style={{
                  width: '100%',
                  padding: '14px 16px 14px 48px',
                  background: focusedField === 'password' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'password' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '12px',
                  fontSize: '16px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'password' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
          </div>

          {/* Submit Button */}
          <button
            onClick={handleSubmit}
            disabled={loading}
            style={{
              width: '100%',
              background: 'linear-gradient(to right, #6366f1, #a855f7, #ec4899)',
              color: 'white',
              padding: '16px',
              borderRadius: '12px',
              fontSize: '18px',
              fontWeight: 'bold',
              border: 'none',
              cursor: loading ? 'not-allowed' : 'pointer',
              opacity: loading ? 0.7 : 1,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '10px',
              transition: 'all 0.3s',
              boxShadow: '0 4px 15px rgba(99, 102, 241, 0.4)'
            }}
            onMouseEnter={(e) => !loading && (e.target.style.transform = 'translateY(-2px)')}
            onMouseLeave={(e) => (e.target.style.transform = 'translateY(0)')}
          >
            {loading ? (
              <>
                <div style={{
                  width: '20px',
                  height: '20px',
                  border: '3px solid rgba(255, 255, 255, 0.3)',
                  borderTop: '3px solid white',
                  borderRadius: '50%',
                  animation: 'spin 0.8s linear infinite'
                }}></div>
                <span>Memeriksa...</span>
              </>
            ) : (
              <>
                <span>Masuk</span>
                <ArrowRight style={{ width: '20px', height: '20px' }} />
              </>
            )}
          </button>

          {/* Register Link */}
          <div style={{
            marginTop: '25px',
            textAlign: 'center',
            fontSize: '14px',
            color: '#4b5563'
          }}>
            Belum punya akun?{' '}
            <a href="/signup" style={{
              color: '#6366f1',
              fontWeight: '600',
              textDecoration: 'none'
            }}>
              Daftar di sini â†’
            </a>
          </div>
        </div>

        {/* Footer */}
        <div style={{
          background: '#f9fafb',
          padding: '20px',
          textAlign: 'center',
          borderTop: '1px solid #e5e7eb'
        }}>
          <p style={{
            color: '#6b7280',
            fontSize: '12px',
            margin: 0
          }}>
            Â© 2025 AI Planner. Semua hak dilindungi.
          </p>
        </div>
      </div>

      {/* CSS Animations */}
      <style>{`
        @keyframes blob {
          0%, 100% {
            transform: translate(0, 0) scale(1);
          }
          25% {
            transform: translate(30px, -50px) scale(1.1);
          }
          50% {
            transform: translate(-20px, 30px) scale(0.9);
          }
          75% {
            transform: translate(50px, 50px) scale(1.05);
          }
        }
        
        @keyframes spin {
          to {
            transform: rotate(360deg);
          }
        }
      `}</style>
    </div>
  );
};

export default Login;
