import React, { useEffect, useState } from "react";
import { getDatabase, ref, onValue, update } from "firebase/database";
import "../assets/Profile.css";

const Profile = () => {
  const [profile, setProfile] = useState({
    nama: "",
    email: "",
    nomor: "",
    jenisKelamin: "",
    password: "",
    bio: "",
    tanggalLahir: "",
    pekerjaan: "",
    lokasi: "",
  });
  const [originalProfile, setOriginalProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [activeTab, setActiveTab] = useState("personal");
  const [showPassword, setShowPassword] = useState(false);
  const [isDark, setIsDark] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);
  const [stats, setStats] = useState({
    totalSchedules: 0,
    totalChats: 0,
    memberSince: "",
  });

  const db = getDatabase();

  useEffect(() => {
    const username = localStorage.getItem("username");
    if (!username) {
      alert("Kamu belum login!");
      window.location.href = "/login";
      return;
    }
    setCurrentUser(username);

    // initialize theme state from localStorage
    try {
      const theme = localStorage.getItem('theme');
      if (theme === 'dark') {
        setIsDark(true);
        document.documentElement.classList.add('dark');
      } else {
        setIsDark(false);
        document.documentElement.classList.remove('dark');
      }
    } catch (e) {
      // ignore
    }

    // Load user profile
    const userRef = ref(db, `users/${username}`);
    const unsubscribe = onValue(userRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        setProfile(data);
        setOriginalProfile(data);
        
        // Set member since (handle both numeric timestamps and ISO strings)
        if (data.createdAt) {
          const ts = Number(data.createdAt);
          const date = !isNaN(ts) ? new Date(ts) : new Date(data.createdAt);
          setStats((prev) => ({
            ...prev,
            memberSince: date.toLocaleDateString("id-ID", {
              year: "numeric",
              month: "long",
            }),
          }));
        }
      }
      setLoading(false);
    });

    // Load statistics
    loadUserStats(username);

    return () => unsubscribe();
  }, [db]);

  // Load user statistics
  const loadUserStats = (username) => {
    // Count schedules
    const schedulesRef = ref(db, `schedules/${username}`);
    onValue(schedulesRef, (snapshot) => {
      const data = snapshot.val();
      const count = data ? Object.keys(data).length : 0;
      setStats((prev) => ({ ...prev, totalSchedules: count }));
    });

    // Count chats
    const chatsRef = ref(db, `chats/${username}`);
    onValue(chatsRef, (snapshot) => {
      const data = snapshot.val();
      const count = data ? Object.keys(data).length : 0;
      setStats((prev) => ({ ...prev, totalChats: count }));
    });
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps

  // Check for changes
  useEffect(() => {
    const changed = JSON.stringify(profile) !== JSON.stringify(originalProfile);
    setHasChanges(changed);
  }, [profile, originalProfile]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    if (!currentUser) return;

    // Validation
    if (!profile.nama?.trim()) {
      alert("‚ö†Ô∏è Nama lengkap harus diisi!");
      return;
    }

    if (!profile.email?.trim()) {
      alert("‚ö†Ô∏è Email harus diisi!");
      return;
    }

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(profile.email)) {
      alert("‚ö†Ô∏è Format email tidak valid!");
      return;
    }

    setSaving(true);
    try {
      const updateData = {
        ...profile,
        updatedAt: new Date().toISOString(),
      };

      await update(ref(db, `users/${currentUser}`), updateData);
      setOriginalProfile(updateData);
      alert("‚úÖ Profil berhasil diperbarui!");
    } catch (error) {
      console.error(error);
      alert("‚ùå Gagal menyimpan profil!");
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    setProfile(originalProfile);
    setHasChanges(false);
  };

  const handleDeleteAccount = () => {
    const confirmed = window.confirm(
      "‚ö†Ô∏è PERINGATAN!\n\nMenghapus akun akan menghapus semua data Anda secara permanen termasuk:\n‚Ä¢ Semua jadwal\n‚Ä¢ Riwayat chat AI\n‚Ä¢ Informasi profil\n\nTindakan ini TIDAK DAPAT dibatalkan!\n\nApakah Anda yakin ingin melanjutkan?"
    );

    if (confirmed) {
      const doubleConfirm = window.confirm(
        "Konfirmasi sekali lagi: Apakah Anda benar-benar yakin ingin menghapus akun?"
      );

      if (doubleConfirm) {
        alert("üöß Fitur hapus akun akan segera tersedia. Silakan hubungi admin untuk bantuan.");
        // TODO: Implement delete account logic
      }
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Memuat data profil...</p>
      </div>
    );
  }

  return (
    <div className="profile-container">
      {/* Profile Header */}
      <div className="profile-header">
        <div className="profile-header-content">
          <div className="profile-avatar-section">
            <div className="profile-avatar-wrapper">
              <div className="profile-avatar">
                {profile.nama?.charAt(0)?.toUpperCase() || "?"}
              </div>
              <button className="avatar-edit-btn" title="Ubah foto profil">
                üì∑
              </button>
            </div>
            <div className="profile-info">
              <h1>{profile.nama || "Nama Belum Diatur"}</h1>
              <p className="profile-username">@{currentUser}</p>
              <div className="profile-badges">
                <span className="badge badge-premium">Premium User</span>
                <span className="badge badge-verified">‚úì Verified</span>
              </div>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="profile-stats">
            <div className="stat-box">
              <div className="stat-icon">üìÖ</div>
              <div className="stat-info">
                <p className="stat-value">{stats.totalSchedules}</p>
                <p className="stat-label">Jadwal</p>
              </div>
            </div>
            <div className="stat-box">
              <div className="stat-icon">üí¨</div>
              <div className="stat-info">
                <p className="stat-value">{stats.totalChats}</p>
                <p className="stat-label">Chat AI</p>
              </div>
            </div>
            <div className="stat-box">
              <div className="stat-icon">üìÜ</div>
              <div className="stat-info">
                <p className="stat-value">{stats.memberSince || "2024"}</p>
                <p className="stat-label">Member Sejak</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="profile-tabs">
        <button
          className={`tab-btn ${activeTab === "personal" ? "active" : ""}`}
          onClick={() => setActiveTab("personal")}
        >
          <span className="tab-icon">üë§</span>
          Informasi Pribadi
        </button>
        <button
          className={`tab-btn ${activeTab === "account" ? "active" : ""}`}
          onClick={() => setActiveTab("account")}
        >
          <span className="tab-icon">üîê</span>
          Keamanan Akun
        </button>
        <button
          className={`tab-btn ${activeTab === "settings" ? "active" : ""}`}
          onClick={() => setActiveTab("settings")}
        >
          <span className="tab-icon">‚öôÔ∏è</span>
          Pengaturan
        </button>
      </div>

      {/* Tab Content */}
      <div className="profile-content">
        {activeTab === "personal" && (
          <div className="profile-section">
            <div className="section-header">
              <h2>üìù Informasi Pribadi</h2>
              <p>Kelola data pribadi dan informasi kontak Anda</p>
            </div>

            <div className="form-grid">
              <div className="form-group full-width">
                <label>Nama Lengkap *</label>
                <input
                  type="text"
                  name="nama"
                  value={profile.nama || ""}
                  onChange={handleChange}
                  placeholder="Masukkan nama lengkap"
                  required
                />
              </div>

              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={profile.email || ""}
                  onChange={handleChange}
                  placeholder="nama@email.com"
                  required
                />
              </div>

              <div className="form-group">
                <label>Nomor WhatsApp</label>
                <input
                  type="tel"
                  name="nomor"
                  value={profile.nomor || ""}
                  onChange={handleChange}
                  placeholder="+62 812-3456-7890"
                />
              </div>

              <div className="form-group">
                <label>Jenis Kelamin</label>
                <select
                  name="jenisKelamin"
                  value={profile.jenisKelamin || ""}
                  onChange={handleChange}
                >
                  <option value="">Pilih jenis kelamin</option>
                  <option value="Laki-laki">Laki-laki</option>
                  <option value="Perempuan">Perempuan</option>
                </select>
              </div>

              <div className="form-group">
                <label>Tanggal Lahir</label>
                <input
                  type="date"
                  name="tanggalLahir"
                  value={profile.tanggalLahir || ""}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label>Pekerjaan</label>
                <input
                  type="text"
                  name="pekerjaan"
                  value={profile.pekerjaan || ""}
                  onChange={handleChange}
                  placeholder="Contoh: Software Engineer"
                />
              </div>

              <div className="form-group">
                <label>Lokasi</label>
                <input
                  type="text"
                  name="lokasi"
                  value={profile.lokasi || ""}
                  onChange={handleChange}
                  placeholder="Contoh: Jakarta, Indonesia"
                />
              </div>

              <div className="form-group full-width">
                <label>Bio</label>
                <textarea
                  name="bio"
                  value={profile.bio || ""}
                  onChange={handleChange}
                  placeholder="Ceritakan sedikit tentang diri Anda..."
                  rows="4"
                />
              </div>
            </div>
          </div>
        )}

        {activeTab === "account" && (
          <div className="profile-section">
            <div className="section-header">
              <h2>üîê Keamanan Akun</h2>
              <p>Kelola password dan keamanan akun Anda</p>
            </div>

            <div className="form-grid">
              <div className="form-group full-width">
                <label>Username</label>
                <input
                  type="text"
                  value={currentUser}
                  disabled
                  className="disabled-input"
                />
                <small className="form-hint">Username tidak dapat diubah</small>
              </div>

              <div className="form-group full-width">
                <label>Password Baru</label>
                <div className="password-input-wrapper">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    value={profile.password || ""}
                    onChange={handleChange}
                    onBlur={async () => {
                      // Update password immediately on blur if changed
                      try {
                        if (!currentUser) return;
                        const newPass = profile.password || "";
                        const oldPass = originalProfile.password || "";
                        if (newPass && newPass !== oldPass) {
                          if (newPass.length < 6) {
                            alert("‚ö†Ô∏è Password minimal 6 karakter.");
                            return;
                          }
                          setSaving(true);
                          await update(ref(db, `users/${currentUser}`), {
                            password: newPass,
                            updatedAt: new Date().toISOString(),
                          });
                          setOriginalProfile((prev) => ({ ...prev, password: newPass }));
                          alert("‚úÖ Password berhasil diperbarui!");
                        }
                      } catch (err) {
                        console.error("Gagal update password:", err);
                        alert("‚ùå Gagal memperbarui password. Coba lagi.");
                      } finally {
                        setSaving(false);
                      }
                    }}
                    placeholder="Masukkan password baru"
                  />
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? "üôà" : "üëÅÔ∏è"}
                  </button>
                </div>
                <small className="form-hint">
                  Minimal 6 karakter. Kombinasikan huruf, angka, dan simbol untuk keamanan lebih baik.
                </small>
              </div>

              <div className="security-info">
                <div className="info-card">
                  <div className="info-icon">üõ°Ô∏è</div>
                  <div className="info-content">
                    <h4>Status Keamanan</h4>
                    <p>Akun Anda aman dan terlindungi</p>
                  </div>
                </div>
                <div className="info-card">
                  <div className="info-icon">üì±</div>
                  <div className="info-content">
                    <h4>Notifikasi WhatsApp</h4>
                    <p>Aktif untuk reminder jadwal</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === "settings" && (
          <div className="profile-section">
            <div className="section-header">
              <h2>‚öôÔ∏è Pengaturan Akun</h2>
              <p>Kelola preferensi dan pengaturan aplikasi</p>
            </div>

            <div className="settings-list">
              {/* Notifikasi Email removed as requested */}

              <div className="setting-item">
                <div className="setting-info">
                  <h4>üì± Notifikasi WhatsApp</h4>
                  <p>Reminder otomatis via WhatsApp</p>
                </div>
                <label className="toggle-switch">
                  <input type="checkbox" defaultChecked />
                  <span className="toggle-slider"></span>
                </label>
              </div>

              <div className="setting-item">
                <div className="setting-info">
                  <h4>üåô Mode Gelap</h4>
                  <p>Tampilan yang nyaman untuk mata</p>
                </div>
                <label className="toggle-switch">
                  <input type="checkbox" checked={isDark} onChange={() => {
                    const next = !isDark;
                    setIsDark(next);
                    try {
                      if (next) {
                        document.documentElement.classList.add('dark');
                        localStorage.setItem('theme', 'dark');
                      } else {
                        document.documentElement.classList.remove('dark');
                        localStorage.setItem('theme', 'light');
                      }
                    } catch (e) {
                      console.warn('Gagal menyimpan theme:', e);
                    }
                  }} />
                  <span className="toggle-slider"></span>
                </label>
              </div>

              <div className="setting-item">
                <div className="setting-info">
                  <h4>ü§ñ Auto Save Chat AI</h4>
                  <p>Simpan percakapan AI secara otomatis</p>
                </div>
                <label className="toggle-switch">
                  <input type="checkbox" defaultChecked />
                  <span className="toggle-slider"></span>
                </label>
              </div>
            </div>

            <div className="danger-zone">
              <h3>‚ö†Ô∏è Zona Berbahaya</h3>
              <p>Tindakan berikut bersifat permanen dan tidak dapat dibatalkan</p>
              <button className="btn-danger" onClick={handleDeleteAccount}>
                üóëÔ∏è Hapus Akun Permanen
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Action Buttons */}
      {hasChanges && (
        <div className="profile-actions">
          <div className="actions-wrapper">
            <button className="btn-cancel" onClick={handleCancel}>
              ‚úï Batal
            </button>
            <button
              className="btn-save"
              onClick={handleSave}
              disabled={saving}
            >
              {saving ? "üíæ Menyimpan..." : "üíæ Simpan Perubahan"}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Profile;
