import React, { useState, useEffect } from "react";
import { getDatabase, ref, onValue, push, set, remove, update } from "firebase/database";
import "../assets/Jadwal.css";

const Jadwal = () => {
  const [schedules, setSchedules] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("");
  const [loading, setLoading] = useState(true);

  // User default (tanpa login)
  const currentUser = "defaultUser";

  const db = getDatabase();

  // Ambil data dari Firebase
  useEffect(() => {
    const scheduleRef = ref(db, `schedules/${currentUser}`);
    const unsubscribe = onValue(scheduleRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const loadedSchedules = Object.entries(data).map(([id, value]) => ({
          id,
          ...value,
        }));
        setSchedules(loadedSchedules);
      } else {
        setSchedules([]);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, [db, currentUser]);

  // Tambah jadwal baru
  const handleAddSchedule = () => {
    const title = prompt("Masukkan nama kegiatan:");
    if (!title) return;

    const newScheduleRef = push(ref(db, `schedules/${currentUser}`));
    const newSchedule = {
      title,
      startTime: new Date().toISOString(),
      endTime: new Date(Date.now() + 3600000).toISOString(),
      status: "pending",
      priority: "normal",
      note: "",
      createdAt: new Date().toISOString(),
    };

    set(newScheduleRef, newSchedule);
  };

  // Edit jadwal
  const handleEditSchedule = (schedule) => {
    const titleBaru = prompt("Ubah nama kegiatan:", schedule.title);
    if (!titleBaru) return;

    update(ref(db, `schedules/${currentUser}/${schedule.id}`), {
      title: titleBaru,
    });
  };

  // Toggle status
  const toggleStatus = (schedule) => {
    const newStatus =
      schedule.status === "done"
        ? "pending"
        : schedule.status === "pending"
        ? "ongoing"
        : "done";

    update(ref(db, `schedules/${currentUser}/${schedule.id}`), {
      status: newStatus,
    });
  };

  // Hapus jadwal
  const handleDeleteSchedule = (scheduleId) => {
    if (window.confirm("Yakin ingin menghapus jadwal ini?")) {
      remove(ref(db, `schedules/${currentUser}/${scheduleId}`));
    }
  };

  const filteredSchedules = schedules.filter((s) => {
    const matchSearch =
      s.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (s.note && s.note.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchStatus = filterStatus === "" || s.status === filterStatus;
    return matchSearch && matchStatus;
  });

  const getStatusClass = (status) => {
    switch (status) {
      case "pending":
        return "pending";
      case "ongoing":
        return "ongoing";
      case "done":
        return "done";
      default:
        return "";
    }
  };

  if (loading) return <p>Memuat data jadwal...</p>;

  return (
    <div className="dashboard-content">
      <div className="page-header">
        <div className="header-content">
          <h1>üóìÔ∏è Daftar Jadwal</h1>
          <p className="header-subtitle">Kelola dan pantau seluruh kegiatan</p>
        </div>
        <div className="header-actions">
          <button className="btn-primary" onClick={handleAddSchedule}>
            ‚ûï Tambah Jadwal
          </button>
        </div>
      </div>

      <div className="search-filter">
        <div className="search-box">
          <input
            type="text"
            placeholder="Cari kegiatan..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <select
          className="filter-select"
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
        >
          <option value="">Semua Status</option>
          <option value="pending">Belum Dilaksanakan</option>
          <option value="ongoing">Sedang Berlangsung</option>
          <option value="done">Selesai</option>
        </select>
      </div>

      <div className="schedules-table">
        <div className="table-header">
          <div className="th">Kegiatan</div>
          <div className="th">Status</div>
          <div className="th">Aksi</div>
        </div>

        <div className="table-body">
          {filteredSchedules.length === 0 ? (
            <div className="table-row">
              <div className="td" style={{ gridColumn: "1 / -1", textAlign: "center", padding: "2rem" }}>
                <p>Tidak ada jadwal ditemukan</p>
              </div>
            </div>
          ) : (
            filteredSchedules.map((schedule) => (
              <div key={schedule.id} className="table-row">
                <div className="td">{schedule.title}</div>
                <div className="td">
                  <span
                    className={`status-badge ${getStatusClass(schedule.status)}`}
                    onClick={() => toggleStatus(schedule)}
                    style={{ cursor: "pointer" }}
                    title="Klik untuk ubah status"
                  >
                    {schedule.status}
                  </span>
                </div>
                <div className="td">
                  <div className="action-buttons">
                    <button
                      className="btn-action edit"
                      title="Edit"
                      onClick={() => handleEditSchedule(schedule)}
                    >
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="btn-action delete"
                      title="Hapus"
                      onClick={() => handleDeleteSchedule(schedule.id)}
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Jadwal;
