import React, { useState } from 'react';
import { Brain, Mail, Lock, Phone, User, Sparkles, ArrowRight, CheckCircle } from 'lucide-react';

const Signup = () => {
  const [nama, setNama] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [focusedField, setFocusedField] = useState('');

  const handleSubmit = () => {
    setError('');
    setSuccess('');

    // Validasi
    if (!nama || !email || !phone || !password || !confirmPassword) {
      setError('Semua field wajib diisi!');
      return;
    }

    if (password !== confirmPassword) {
      setError('Password dan konfirmasi password tidak cocok!');
      return;
    }

    if (password.length < 6) {
      setError('Password minimal 6 karakter!');
      return;
    }

    // Validasi email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Format email tidak valid!');
      return;
    }

    // Validasi nomor HP
    if (phone.length < 10) {
      setError('Nomor WhatsApp minimal 10 digit!');
      return;
    }

    setLoading(true);

    // Simulasi register (ganti dengan Firebase logic kamu)
    setTimeout(() => {
      setSuccess('Akun berhasil dibuat! Redirecting ke login...');
      setTimeout(() => {
        window.location.href = '/login';
      }, 1500);
    }, 2000);
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden'
    }}>
      
      {/* Animated Blobs */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '300px',
        height: '300px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '50%',
        filter: 'blur(60px)',
        animation: 'blob 7s infinite'
      }}></div>
      
      <div style={{
        position: 'absolute',
        bottom: 0,
        right: 0,
        width: '300px',
        height: '300px',
        background: 'rgba(255, 200, 255, 0.1)',
        borderRadius: '50%',
        filter: 'blur(60px)',
        animation: 'blob 7s infinite 3s'
      }}></div>

      {/* Register Card */}
      <div style={{
        width: '100%',
        maxWidth: '500px',
        background: 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(20px)',
        borderRadius: '24px',
        boxShadow: '0 25px 50px rgba(0, 0, 0, 0.25)',
        overflow: 'hidden',
        position: 'relative',
        zIndex: 10
      }}>
        
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #6366f1 0%, #a855f7 50%, #ec4899 100%)',
          padding: '35px 30px',
          textAlign: 'center',
          position: 'relative'
        }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '70px',
            height: '70px',
            background: 'rgba(255, 255, 255, 0.2)',
            borderRadius: '18px',
            marginBottom: '12px'
          }}>
            <Brain style={{ width: '36px', height: '36px', color: 'white' }} strokeWidth={2.5} />
          </div>
          
          <h1 style={{
            fontSize: '28px',
            fontWeight: 'bold',
            color: 'white',
            margin: '0 0 8px 0'
          }}>
            Daftar AI Planner
          </h1>
          
          <div style={{
            color: 'rgba(255, 255, 255, 0.9)',
            fontSize: '14px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '8px'
          }}>
            <Sparkles style={{ width: '14px', height: '14px' }} />
            <span>Mulai kelola jadwal dengan AI</span>
            <Sparkles style={{ width: '14px', height: '14px' }} />
          </div>
        </div>

        {/* Form Section */}
        <div style={{ padding: '30px', maxHeight: '500px', overflowY: 'auto' }}>
          <h2 style={{
            fontSize: '22px',
            fontWeight: 'bold',
            color: '#1f2937',
            textAlign: 'center',
            margin: '0 0 6px 0'
          }}>
            Buat Akun Baru 🚀
          </h2>
          
          <p style={{
            color: '#6b7280',
            fontSize: '13px',
            textAlign: 'center',
            margin: '0 0 22px 0'
          }}>
            Isi data Anda untuk memulai
          </p>

          {/* Error Message */}
          {error && (
            <div style={{
              background: 'linear-gradient(to right, #ef4444, #ec4899)',
              color: 'white',
              padding: '13px',
              borderRadius: '12px',
              fontSize: '13px',
              marginBottom: '18px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <div style={{
                width: '6px',
                height: '6px',
                background: 'white',
                borderRadius: '50%'
              }}></div>
              {error}
            </div>
          )}

          {/* Success Message */}
          {success && (
            <div style={{
              background: 'linear-gradient(to right, #10b981, #059669)',
              color: 'white',
              padding: '13px',
              borderRadius: '12px',
              fontSize: '13px',
              marginBottom: '18px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <CheckCircle style={{ width: '18px', height: '18px' }} />
              {success}
            </div>
          )}

          {/* Nama Lengkap */}
          <div style={{ marginBottom: '16px' }}>
            <label style={{
              display: 'block',
              fontSize: '13px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '6px'
            }}>
              Nama Lengkap
            </label>
            <div style={{ position: 'relative' }}>
              <User style={{
                position: 'absolute',
                left: '14px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '18px',
                height: '18px',
                color: focusedField === 'nama' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="text"
                placeholder="Masukkan nama lengkap"
                value={nama}
                onChange={(e) => setNama(e.target.value)}
                onFocus={() => setFocusedField('nama')}
                onBlur={() => setFocusedField('')}
                style={{
                  width: '100%',
                  padding: '12px 14px 12px 44px',
                  background: focusedField === 'nama' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'nama' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '10px',
                  fontSize: '15px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'nama' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
          </div>

          {/* Email */}
          <div style={{ marginBottom: '16px' }}>
            <label style={{
              display: 'block',
              fontSize: '13px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '6px'
            }}>
              Email
            </label>
            <div style={{ position: 'relative' }}>
              <Mail style={{
                position: 'absolute',
                left: '14px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '18px',
                height: '18px',
                color: focusedField === 'email' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="email"
                placeholder="nama@email.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                onFocus={() => setFocusedField('email')}
                onBlur={() => setFocusedField('')}
                style={{
                  width: '100%',
                  padding: '12px 14px 12px 44px',
                  background: focusedField === 'email' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'email' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '10px',
                  fontSize: '15px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'email' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
          </div>

          {/* Nomor WhatsApp */}
          <div style={{ marginBottom: '16px' }}>
            <label style={{
              display: 'block',
              fontSize: '13px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '6px'
            }}>
              Nomor WhatsApp 📱
            </label>
            <div style={{ position: 'relative' }}>
              <Phone style={{
                position: 'absolute',
                left: '14px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '18px',
                height: '18px',
                color: focusedField === 'phone' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="tel"
                placeholder="08123456789"
                value={phone}
                onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ''))}
                onFocus={() => setFocusedField('phone')}
                onBlur={() => setFocusedField('')}
                style={{
                  width: '100%',
                  padding: '12px 14px 12px 44px',
                  background: focusedField === 'phone' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'phone' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '10px',
                  fontSize: '15px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'phone' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
            <p style={{ fontSize: '11px', color: '#6b7280', marginTop: '4px', marginLeft: '2px' }}>
              Untuk notifikasi reminder via WhatsApp
            </p>
          </div>

          {/* Password */}
          <div style={{ marginBottom: '16px' }}>
            <label style={{
              display: 'block',
              fontSize: '13px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '6px'
            }}>
              Password
            </label>
            <div style={{ position: 'relative' }}>
              <Lock style={{
                position: 'absolute',
                left: '14px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '18px',
                height: '18px',
                color: focusedField === 'password' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="password"
                placeholder="Minimal 6 karakter"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onFocus={() => setFocusedField('password')}
                onBlur={() => setFocusedField('')}
                style={{
                  width: '100%',
                  padding: '12px 14px 12px 44px',
                  background: focusedField === 'password' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'password' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '10px',
                  fontSize: '15px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'password' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
          </div>

          {/* Confirm Password */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{
              display: 'block',
              fontSize: '13px',
              fontWeight: '600',
              color: '#374151',
              marginBottom: '6px'
            }}>
              Konfirmasi Password
            </label>
            <div style={{ position: 'relative' }}>
              <Lock style={{
                position: 'absolute',
                left: '14px',
                top: '50%',
                transform: 'translateY(-50%)',
                width: '18px',
                height: '18px',
                color: focusedField === 'confirm' ? '#6366f1' : '#9ca3af'
              }} />
              <input
                type="password"
                placeholder="Ulangi password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                onFocus={() => setFocusedField('confirm')}
                onBlur={() => setFocusedField('')}
                style={{
                  width: '100%',
                  padding: '12px 14px 12px 44px',
                  background: focusedField === 'confirm' ? 'white' : '#f9fafb',
                  border: `2px solid ${focusedField === 'confirm' ? '#6366f1' : '#e5e7eb'}`,
                  borderRadius: '10px',
                  fontSize: '15px',
                  outline: 'none',
                  transition: 'all 0.3s',
                  boxShadow: focusedField === 'confirm' ? '0 4px 12px rgba(99, 102, 241, 0.15)' : 'none'
                }}
              />
            </div>
          </div>

          {/* Submit Button */}
          <button
            onClick={handleSubmit}
            disabled={loading}
            style={{
              width: '100%',
              background: 'linear-gradient(to right, #6366f1, #a855f7, #ec4899)',
              color: 'white',
              padding: '15px',
              borderRadius: '12px',
              fontSize: '17px',
              fontWeight: 'bold',
              border: 'none',
              cursor: loading ? 'not-allowed' : 'pointer',
              opacity: loading ? 0.7 : 1,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '10px',
              transition: 'all 0.3s',
              boxShadow: '0 4px 15px rgba(99, 102, 241, 0.4)'
            }}
            onMouseEnter={(e) => !loading && (e.target.style.transform = 'translateY(-2px)')}
            onMouseLeave={(e) => (e.target.style.transform = 'translateY(0)')}
          >
            {loading ? (
              <>
                <div style={{
                  width: '18px',
                  height: '18px',
                  border: '3px solid rgba(255, 255, 255, 0.3)',
                  borderTop: '3px solid white',
                  borderRadius: '50%',
                  animation: 'spin 0.8s linear infinite'
                }}></div>
                <span>Membuat Akun...</span>
              </>
            ) : (
              <>
                <span>Daftar Sekarang</span>
                <ArrowRight style={{ width: '18px', height: '18px' }} />
              </>
            )}
          </button>

          {/* Login Link */}
          <div style={{
            marginTop: '22px',
            textAlign: 'center',
            fontSize: '13px',
            color: '#4b5563'
          }}>
            Sudah punya akun?{' '}
            <a href="/login" style={{
              color: '#6366f1',
              fontWeight: '600',
              textDecoration: 'none'
            }}>
              Masuk di sini →
            </a>
          </div>
        </div>

        {/* Footer */}
        <div style={{
          background: '#f9fafb',
          padding: '16px',
          textAlign: 'center',
          borderTop: '1px solid #e5e7eb'
        }}>
          <p style={{
            color: '#6b7280',
            fontSize: '11px',
            margin: 0
          }}>
            © 2025 AI Planner. Semua hak dilindungi.
          </p>
        </div>
      </div>

      {/* CSS Animations */}
      <style>{`
        @keyframes blob {
          0%, 100% {
            transform: translate(0, 0) scale(1);
          }
          25% {
            transform: translate(30px, -50px) scale(1.1);
          }
          50% {
            transform: translate(-20px, 30px) scale(0.9);
          }
          75% {
            transform: translate(50px, 50px) scale(1.05);
          }
        }
        
        @keyframes spin {
          to {
            transform: rotate(360deg);
          }
        }
      `}</style>
    </div>
  );
};

export default Signup;
