import React, { useState, useEffect } from "react";
import { getDatabase, ref, onValue, push, set, remove, update } from "firebase/database";
import "../assets/Jadwal.css";

const Jadwal = () => {
  const [schedules, setSchedules] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("");
  const [loading, setLoading] = useState(true);

  const db = getDatabase();

  // 🔑 Ambil user login dari localStorage
  const currentUser = localStorage.getItem("currentUser");

  useEffect(() => {
    // Jika belum login, arahkan ke halaman login
    if (!currentUser) {
      alert("Silakan login terlebih dahulu!");
      window.location.href = "/login";
      return;
    }

    // Ambil data jadwal user
    const scheduleRef = ref(db, `schedules/${currentUser}`);
    const unsubscribe = onValue(scheduleRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const loaded = Object.entries(data).map(([id, value]) => ({
          id,
          ...value,
        }));
        setSchedules(loaded);
      } else {
        setSchedules([]);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, [db, currentUser]);

  // ➕ Tambah Jadwal
  const handleAddSchedule = () => {
    const title = prompt("Nama kegiatan:");
    if (!title) return;

    const newScheduleRef = push(ref(db, `schedules/${currentUser}`));
    const newSchedule = {
      title,
      startTime: new Date().toISOString().slice(0, 16),
      endTime: new Date(Date.now() + 3600000).toISOString().slice(0, 16),
      status: "pending",
      priority: "normal",
      note: "-",
      createdAt: new Date().toISOString(),
    };

    set(newScheduleRef, newSchedule);
  };

  // 🗑️ Hapus Jadwal
  const handleDeleteSchedule = (scheduleId) => {
    if (window.confirm("Yakin ingin menghapus jadwal ini?")) {
      remove(ref(db, `schedules/${currentUser}/${scheduleId}`));
    }
  };

  // ✏️ Edit Jadwal
  const handleEditSchedule = (schedule) => {
    const newTitle = prompt("Ubah nama kegiatan:", schedule.title);
    if (!newTitle) return;
    update(ref(db, `schedules/${currentUser}/${schedule.id}`), { title: newTitle });
  };

  // ✅ Ubah Status
  const toggleStatus = (schedule) => {
    const newStatus = schedule.status === "done" ? "pending" : "done";
    update(ref(db, `schedules/${currentUser}/${schedule.id}`), { status: newStatus });
  };

  // 🔍 Filter & Search
  const filteredSchedules = schedules.filter((s) => {
    const matchSearch = s.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchStatus = filterStatus === "" || s.status === filterStatus;
    return matchSearch && matchStatus;
  });

  const getStatusClass = (status) => {
    switch (status) {
      case "pending":
        return "pending";
      case "ongoing":
        return "ongoing";
      case "done":
        return "done";
      default:
        return "";
    }
  };

  if (loading) return <p>⏳ Memuat data jadwal...</p>;

  return (
    <div className="dashboard-content">
      <div className="page-header">
        <div className="header-content">
          <h1>🗓️ Jadwal Kegiatan</h1>
          <p className="header-subtitle">Kelola kegiatan dan rencana harian Anda</p>
        </div>
        <div className="header-actions">
          <button className="btn-primary" onClick={handleAddSchedule}>
            ➕ Tambah Jadwal
          </button>
        </div>
      </div>

      <div className="schedules-section">
        <div className="section-header">
          <div className="search-filter">
            <div className="search-box">
              <input
                type="text"
                placeholder="Cari kegiatan..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <select
              className="filter-select"
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
            >
              <option value="">Semua Status</option>
              <option value="pending">Belum Selesai</option>
              <option value="ongoing">Sedang Berlangsung</option>
              <option value="done">Selesai</option>
            </select>
          </div>
        </div>

        <div className="schedules-table">
          <div className="table-header">
            <div className="th">Nama Kegiatan</div>
            <div className="th">Mulai</div>
            <div className="th">Selesai</div>
            <div className="th">Status</div>
            <div className="th">Aksi</div>
          </div>

          <div className="table-body">
            {filteredSchedules.length === 0 ? (
              <div className="table-row">
                <div className="td" style={{ gridColumn: "1 / -1", textAlign: "center", padding: "2rem" }}>
                  <p>Tidak ada jadwal ditemukan</p>
                </div>
              </div>
            ) : (
              filteredSchedules.map((schedule) => (
                <div key={schedule.id} className="table-row">
                  <div className="td">{schedule.title}</div>
                  <div className="td">{schedule.startTime?.replace("T", " ")}</div>
                  <div className="td">{schedule.endTime?.replace("T", " ")}</div>
                  <div className="td">
                    <span className={`status-badge ${getStatusClass(schedule.status)}`}>
                      {schedule.status}
                    </span>
                  </div>
                  <div className="td">
                    <div className="action-buttons">
                      <button className="btn-action" title="Ubah status" onClick={() => toggleStatus(schedule)}>
                        {schedule.status === "done" ? "🔁" : "✅"}
                      </button>
                      <button className="btn-action edit" title="Edit" onClick={() => handleEditSchedule(schedule)}>
                        ✏️
                      </button>
                      <button className="btn-action delete" title="Hapus" onClick={() => handleDeleteSchedule(schedule.id)}>
                        🗑️
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Jadwal;
